<template>
	<view
		class="u-avatar"
		:class="[`u-avatar--${shape}`]"
		:style="[{
			backgroundColor: (text || icon) ? (randomBgColor ? colors[colorIndex !== '' ? colorIndex : $u.random(0, 19)] : bgColor) : 'transparent',
			width: $u.addUnit(size),
			height: $u.addUnit(size),
		}, $u.addStyle(customStyle)]"
		@tap="clickHandler"
	>
		<slot>
			<!-- #ifdef MP-WEIXIN || MP-QQ || MP-BAIDU  -->
			<open-data
				v-if="mpAvatar && allowMp"
				type="userAvatarUrl"
				:style="[{
					width: $u.addUnit(size),
					height: $u.addUnit(size)
				}]"
			/>
			<!-- #endif -->
			<!-- #ifndef MP-WEIXIN && MP-QQ && MP-BAIDU  -->
			<template v-if="mpAvatar && allowMp"></template>
			<!-- #endif -->
			<u-icon
				v-else-if="icon"
				:name="icon"
				:size="fontSize"
				:color="color"
			></u-icon>
			<u--text
				v-else-if="text"
				:text="text"
				:size="fontSize"
				:color="color"
				align="center"
				customStyle="justify-content: center"
			></u--text>
			<image
				class="u-avatar__image"
				v-else
				:class="[`u-avatar__image--${shape}`]"
				:src="avatarUrl || defaultUrl"
				:mode="mode"
				@error="errorHandler"
				:style="[{
					width: $u.addUnit(size),
					height: $u.addUnit(size)
				}]"
			></image>
		</slot>
	</view>
</template>

<script>
	import props from './props.js';
	const base64Avatar =
		"data:image/png;base64,"
	/**
	 * Avatar  
	 * @description 
	 * @tutorial https://www.uviewui.com/components/avatar.html
	 *
	 * @property {String}			src				()
	 * @property {String}			shape			   circle () | square
	 * @property {String | Number}	size			(large, default, mini)  40 
	 * @property {String}			mode			uniimagemodewidthFix  'scaleToFill' 
	 * @property {String}			text			src
	 * @property {String}			bgColor			  '#c0c4cc' 
	 * @property {String}			color			  '#ffffff' 
	 * @property {String | Number}	fontSize		   18 
	 * @property {String}			icon			
	 * @property {Boolean}			mpAvatar		QQ   false 
	 * @property {Boolean}			randomBgColor	   false 
	 * @property {String}			defaultUrl		()
	 * @property {String | Number}	colorIndex		randomBgColortrue0-19
	 * @property {String}			name			   'level' 
	 * @property {Object}			customStyle		
	 *
	 * @event    {Function}        click          index: 
	 * @example  <u-avatar :src="src" mode="square"></u-avatar>
	 */
	export default {
		name: 'u-avatar',
		mixins: [uni.$u.mpMixin, uni.$u.mixin, props],
		data() {
			return {
				// randomBgColortrue
				colors: ['#ffb34b', '#f2bba9', '#f7a196', '#f18080', '#88a867', '#bfbf39', '#89c152', '#94d554', '#f19ec2',
					'#afaae4', '#e1b0df', '#c38cc1', '#72dcdc', '#9acdcb', '#77b1cc', '#448aca', '#86cefa', '#98d1ee',
					'#73d1f1',
					'#80a7dc'
				],
				avatarUrl: this.src,
				allowMp: false
			}
		},
		watch: {
			// srcavatarUrlsrc
			// props
			src: {
				immediate: true,
				handler(newVal) {
					this.avatarUrl = newVal
					// srcerrorsrc''
					if(!newVal) {
						this.errorHandler()
					}
				}
			}
		},
		computed: {
			imageStyle() {
				const style = {}
				return style
			}
		},
		created() {
			this.init()
		},
		methods: {
			init() {
				// open-data
				// uni.getUserInfo()
				// 
				// #ifdef MP-WEIXIN || MP-QQ || MP-BAIDU
				this.allowMp = true
				// #endif
			},
			// name"/"
			isImg() {
				return this.src.indexOf('/') !== -1
			},
			// 
			errorHandler() {
				this.avatarUrl = this.defaultUrl || base64Avatar
			},
			clickHandler() {
				this.$emit('click', this.name)
			}
		}
	}
</script>

<style lang="scss" scoped>
	@import "../../libs/css/components.scss";

	.u-avatar {
		@include flex;
		align-items: center;
		justify-content: center;

		&--circle {
			border-radius: 100px;
		}

		&--square {
			border-radius: 4px;
		}

		&__image {
			&--circle {
				border-radius: 50%;
			}

			&--square {
				border-radius: 4px;
			}
		}
	}
</style>
